package Exercise;

public class datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer:" + Integer.SIZE + "bit" );
		System.out.println("Short:" + Short.SIZE + "bit");
		System.out.println("Long:" + Long.SIZE + "bit");
		System.out.println("Byte:" + Byte.SIZE + "bit");
		System.out.println("Character:" + Character.SIZE + "bit");
		System.out.println("Float:" + Float.SIZE + "bit");
		System.out.println("Double:" + Double.SIZE + "bit");
		//System.out.println("Boolean:"+Boolean);
		
		System.out.println("\n");
		
		System.out.println("Integer:" + Integer.SIZE/8 + "byte" );
		System.out.println("Short:" + Short.SIZE/8 + "byte");
		System.out.println("Long:" + Long.SIZE/8 + "byte");
		System.out.println("Byte:" + Byte.SIZE/8 + "byte");
		System.out.println("Character:" + Character.SIZE/8 + "byte");
		System.out.println("Float:" + Float.SIZE/8 + "byte");
		System.out.println("Double:" + Double.SIZE/8 + "byte");
		
	}

}
