package Exercise;

public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i,j,x,y;
		 i = 0;
		 j = 0;
		 x = i++;
		 System.out.print("i = "+i+"\n");
		 y = ++j;
		 System.out.print("j = "+j+"\n");
		 System.out.print("x = "+x+"\n");
		 System.out.print("y = "+y+"\n");
		 
		 
	}

}
